option.value:
plugin: aws_ec2
regions:
  - us-east-1
keyed_groups:
  - key: tags.Role
    prefix: tag_Role_
hostnames:
  - private_ip_address
compose:
  ansible_host: >-
    {%- if tags.Role == "Bastion" -%}
    {{ public_ip_address }}
    {%- else -%}
    {{ private_ip_address }}
    {%- endif -%}
  ansible_ssh_common_args: >-
    {%- if tags.Role != "Bastion" -%}
    '-o ProxyCommand="ssh -W %h:%p -i {{ lookup('env', 'HOME') }}/.ssh/vockey.pem admin@{{ groups['tag_Role_Bastion'][0] }}"'
    {%- endif -%}

